const KEYCODES = {
  backspace: 'Backspace',

  escape: 'Escape',

  left: 'ArrowLeft',
  up: 'ArrowUp',
  right: 'ArrowRight',
  down: 'ArrowDown',

  left_square_bracket: '[',
  right_square_bracket: ']',

  spacebar: ' '
}

const swap = obj => {
  let result = {}

  for (let key in obj){
    result[obj[key]] = key
  }

  return result
}

const KEYMAP = swap(KEYCODES)

export default KEYMAP