import {
  RESOLUTION,
  PIXEL_SIZE,
  DEFAULT_COLORS
} from 'config/constants'

const renderPixels = ({ ctx, pixels, color = null /* For overrides */ }) => {
  for (let x in pixels) {
    for (let y in pixels[x]) {
      let _color = pixels[x][y]

      ctx.fillStyle = color || _color

      ctx.fillRect(x * PIXEL_SIZE, y * PIXEL_SIZE, PIXEL_SIZE, PIXEL_SIZE)
    }
  }
}

export {
  renderPixels
}