import { PIXEL_SIZE } from 'config/constants'

function getPixelPosition(pos) {
  return Math.floor(pos / PIXEL_SIZE)
}

function getPixelPositions(evt) {
  return {
    x: getPixelPosition(evt.offsetX),
    y: getPixelPosition(evt.offsetY)
  }
}

export { getPixelPosition }

export default getPixelPositions
