import { RESOLUTION } from 'config/constants'

// pixels = {
//   '-10': { '15': 'color'},
//   '1': { '20': 'color' }
// }

const defaultProps = {
  x: 0,
  y: 0,
  get pixels() { return {} }
}

class PixelGrid {
  constructor(props) {
    let _props = { ...defaultProps, ...props }

    for (let name of Object.getOwnPropertyNames(_props)) {
      this[name] = _props[name]
    }
  }

  get(x, y) {
    let arr = this.pixels[x]

    return arr ? arr[y] : null
  }

  set(x, y, value) {
    if (typeof this.pixels[x] === 'object') {
      this.pixels[x][y] = value
    } else {
      this.pixels[x] = { [y]: value }
    }

    this.pixels
  }

  duplicate() {

  }

  reset() {

  }

  get bounds() {
    return [0, 0, RESOLUTION, RESOLUTION]
  }
}

export default PixelGrid