<script>
  import { project } from 'store'
</script>

<div class="flex justify-between">
  <div class="flex">
    <button class="focus:outline-none bg-blue-600 text-white px-3 py-1 text-sm" on:click|stopPropagation={ () => $project.addFrame() }>Add</button>
    <button class="focus:outline-none bg-orange-500 text-white px-3 py-1 text-sm ml-3 disabled:opacity-50" disabled={ $project.frames.length <= 1 } on:click|stopPropagation={ $project.deleteCurrentFrame }>Delete</button>
    <button class="focus:outline-none bg-gray-400 px-3 py-1 text-sm ml-3" on:click|stopPropagation={ () => $project.duplicateFrame() }>Duplicate</button>
  </div>

  <div class="flex">
    <button class="focus:outline-none bg-black text-white px-3 py-1 text-sm" on:click|stopPropagation={ $project.clearFrame }>Clear</button>
    <button class="focus:outline-none bg-red-600 text-white px-3 py-1 text-sm ml-3" on:click|stopPropagation={ $project.reset }>Reset</button>
  </div>
</div>