<script>
  import { project } from 'store'

  function setFrameRate(evt) {
    $project.setFrameRate(evt.target.value)
  }
</script>

<div class="flex text-xl select-none">
  FPS:&nbsp;<input type="number" min="1" max="60" step="0.25" bind:value={ $project.frameRate } on:change|stopPropagation={ setFrameRate } class="w-auto appearance-none border-0 bg-transparent focus:outline-none">
</div>