<script>
  import { SortableList } from 'components'

  import { project } from 'store'

  function sortFrames(evt) {
    const { oldIndex, newIndex } = evt.detail

    $project.sortFrames(oldIndex, newIndex)
  }
</script>

<div class="w-full max-w-3xl select-none">
  <SortableList
    className="flex flex-wrap { $project.frames.length <= 10 && 'justify-center' }"
    key='id'
    list={ $project.frames }
    on:sort={ sortFrames }
    let:item={ frame }
    let:index
  >
    <div
      on:mousedown={ () => $project.frameIndex = index }
      class="relative m-1 w-16 h-16 bg-white border-2 border-gray-400 { index == $project.frameIndex && 'border-green-400' }"
    >
      {#if frame.repeat > 1 }
        <div
          class="absolute top-0 right-0 px-1 rounded-full bg-white border flex items-center justify-center shadow-md"
          style="min-height: 20px; min-width: 20px; margin-top: -10px; margin-right: -10px;"
        >
          <span class="text-xs text-gray-800">{ frame.repeat }</span>
        </div>
      {/if}

      <img src={ frame.image.src } class="h-full w-full pointer-events-none" alt="frame-{ 'hs' }">
    </div>
  </SortableList>
</div>