<script>
  import { project } from 'store'

  $: invalidTitle = $project.title.length == 0
</script>

<div class="flex justify-between">
  <input bind:value={ $project.title } required class="bg-transparent focus:outline-none focus:border-gray-500 border px-1 { invalidTitle && 'border-red-400 focus:border-red-600' }">

  <div class="flex text-gray-600">
    <button
      on:click={ () => $project.showGrid = !$project.showGrid }
      class="focus:outline-none border-2 p-1 border-gray-400 text-sm { $project.showGrid && 'bg-gray-400' }"
    >
      <svg class="stroke-current text-gray-500 w-5 h-5 { $project.showGrid && 'text-gray-800' }" viewBox="0 0 110 110" fill="none" xmlns="http://www.w3.org/2000/svg">
        <path d="M5 38.5H105M5 71.5H105M72.8571 5V105M39.1837 5V105M5 5H105V105H5L5 5Z" stroke-width="10"/>
      </svg>
    </button>

    <button
      on:click={ () => $project.showUnderlay = !$project.showUnderlay }
      class="focus:outline-none border-2 border-gray-600 px-2 text-sm ml-3 leading-none { $project.showUnderlay && 'bg-teal-500 text-white border-teal-500' }"
    >
      Underlay
    </button>

    <button disabled={ invalidTitle } on:click={ $project.exportGif } class="focus:outline-none bg-purple-600 text-white px-3 py-1 text-sm ml-3 leading-none  { invalidTitle && 'opacity-50' }">Export GIF</button>
  </div>
</div>