/*

Pixels look like this:

  h: {
    0: [0, 1, 2, 3, 4],
    1: [1],
    2: [1, 2, 3, 4]
  }

*/

class Character {
  constructor({ character, pixels }) {
    this.character = character
    this.pixels = pixels
  }

  get width() {
    // Space
    if (this.character === ' ') {
      return 1
    }
    else if (this.character === 'Tab') {
      return 3
    }

    let points = Object.keys(this.pixels).map(Number)

    return points[points.length - 1] + 1
  }
}

export default Character