import {
  RESOLUTION
} from 'config/constants'

import Tool from './Tool'

import { getPixelPositions } from 'lib'

class Brush extends Tool {
  constructor() {
    super()

    this.selecting = false

    this.name = 'brush'
  }

  draw(evt) {
    const { x, y } = getPixelPositions(evt)

    let color = this.project.currentFrame.getPixel(x, y) === this.project.color ? null : this.project.color

    let newColor = this.selecting ? this.selectingColor : color

    if (!this.selecting) {
      this.selecting = true

      this.selectingColor = color
    }

    this.project.currentFrame.setPixel(x, y, newColor)
  }

  mouseDown(evt) {
    return this.draw(evt)
  }

  mouseMove(evt) {
    const isMouseDown = evt.buttons == 1

    if (!isMouseDown) { return false }

    return this.draw(evt)
  }

  mouseUp(evt) {
    this.selecting = false
  }
}

// Mad instance life
export default new Brush()