class Tool {
  constructor() {
    this.mouseDown = this.mouseDown.bind(this)
    this.mouseMove = this.mouseMove.bind(this)
    this.mouseUp = this.mouseUp.bind(this)

    this.name = this.constructor.name.toLowerCase()

    this.cursor = 'default'
  }

  mouseDown(evt) {
    throw new Error('Please implement mouseDown.')
  }

  mouseMove(evt) {
    throw new Error('Please implement mouseMove.')
  }

  mouseUp(evt) {
    throw new Error('Please implement mouseUp.')
  }

  render(ctx) { return false }
}

export default Tool